INSERT INTO forum_topics (category_id, author_id, title, content, views_count, created_at, updated_at) VALUES
(1, 1, 'Как работать с клиентом после травмы плеча?', 'Коллеги, поделитесь опытом! Пришел клиент через 2 месяца после вывиха плеча. Врач разрешил массаж. С чего начать? Какие зоны можно массировать активно, а где быть осторожнее?', 127, '2025-11-20 10:30:00', '2026-01-15 14:20:00'),
(1, 5, 'Клиент засыпает во время сеанса - это нормально?', 'У меня постоянно клиенты засыпают прямо на столе, особенно при классическом массаже. Это хорошо или плохо? Может, я слишком мягко работаю?', 89, '2025-12-02 15:45:00', '2026-01-10 11:30:00'),
(3, 4, 'Расшифровка МРТ: как этот инструмент вам помогает?', 'Начал пользоваться инструментом расшифровки МРТ на платформе. Реально удобно! Раньше часами гуглил термины, теперь всё сразу понятно. Кто еще пользуется?', 203, '2025-11-28 11:15:00', '2026-01-17 15:45:00'),
(4, 9, 'Профессиональное выгорание: как справляетесь?', 'Последние месяцы чувствую усталость, не хочется работать. Раньше любила массаж, а сейчас через силу. Это выгорание? Как с этим быть?', 187, '2025-12-01 09:30:00', '2026-01-16 14:30:00'),
(6, 11, 'Знакомимся! Кто откуда?', 'Давайте познакомимся! Расскажите о себе: откуда вы, сколько работаете массажистом, какое направление любимое?', 215, '2025-11-22 14:00:00', '2026-01-17 16:30:00');